  const renderedList = movies
        .filter(movie => movie.poster_path !== null)
        .map((movie,key) => {
            return(
                <div key={key} /*style={{display:'inline-flex', flexDirection:'column'}} */ >
                <MovieCard image={`https://image.tmdb.org/t/p/w185${movie.poster_path}`} id={movie.id} title={movie.title}/> 
                <Favorite />
            </div>

            )
        })




    return (
        <span className="movie-rendered-list">
            {renderedList}
        </span>  
    )
}
}

export default MovieList
