import React, {useState, useEffect} from 'react'
import './Movies.css'
import FavoriteIcon from '../../images/favorite-icon.png'
import ListIcon from '../../images/list.png'
import {Link} from 'react-router-dom'


const favoritePath = {
    favorite: 's',
    unfavorite: 'r'
}


const MovieCard =  (props) => {
    const [clicked, setClicked] = useState(false)
    const [movie, setMovie] = useState([])
    const [movieName, setMovieName] = useState("")

    const handleClick = React.useCallback((e) => {
        let id = e.currentTarget.id
        setClicked(!clicked); 
        setMovie(prevMovies => {
            const nextMovie = [...prevMovies, id]
            console.log(nextMovie)
            return nextMovie
        })
        console.log(movie)


    },[])




    return (



        <div className="movie-list">
           <div className=" movie-info">
            <Link to={`/info/${props.id}/${encodeURIComponent(props.title)}`}>
                <h3 className="movie-title">{props.title}</h3>
                <img src = {props.image} style={{display:'flex', width:'40%'}}/>
            </Link>

            </div>

        <div style={{marginRight:'50%', marginLeft:'13%', marginTop:'20px', width:'50%', display:'flex', justifyContent:'start'}}>
        <button value={movieName} style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} onClick={handleClick} id={props.id}> 
                <i class={`fa${clicked ? 's' : 'r'} fa-heart fa-2x`}></i>
           </button>
            <button style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} id={props.id}>
                <i class="fas fa-list fa-2x"></i>
            </button>
         </div>



        </div>  
    )
}

export default MovieCard
