import React from 'react'
import moment from 'moment'
import { withAuthorization } from '../../session'
import {withFirebase} from '../../components/firebase'
import {compose} from 'recompose'
import MovieList from '../../components/Movies/MovieList'

let newArr = []
let fil = []


class FavoriteList extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            fil: []
        }
    }

    componentDidUpdate(){

    }



render(){
if(this.props.loggedin && this.props.user === this.props.firebase.auth.currentUser.uid){
    const date = moment(new Date()).format("YYYY-MM-DD")


    this.props.firebase.item(this.props.user).on("child_added", snap => {
        newArr.push({id: snap.val().id, title:snap.val().title, release_date: snap.val().release_date, poster_path:snap.val().poster_path})
        this.setState({
            fil: Array.from(new Set(newArr.map(i => i.id))).map(id => {return newArr.find(a => a.id === id)}) 
        })
    })
    console.log(fil)




    let list = JSON.parse(localStorage.getItem("favoriteList"))

    const soon = fil.filter(l => l.release_date > date)
    const already_released = fil.filter(r => r.release_date < date)


    return (
        <div>


            <h3>My Favorites</h3>
            {fil.length === 0 ? <p>Go back and add some favorites</p> : 
                <>
                {already_released.length !== 0 ? (<><h3>Past Releases</h3>
                <MovieList movies={already_released} isLoggedIn={this.props.loggedin} userID={this.props.user}/> </>) : null}

                 {soon.length !== 0 ? (<><h3>Future Releases</h3>
                <MovieList movies={soon} isLoggedIn={this.props.loggedin} userID={this.props.user}/> </>) : null}   

                </>
            }

        </div>
    )
        }
        else{
            return null
        }
}
}


const condition = authUser => !!authUser;
export default compose(
    withAuthorization(condition),
    withFirebase
)(FavoriteList);
