import React, { useState, useEffect } from "react";
import Search from "../../components/Search";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Logo from "../../components/TMDB-Image";
import "../../components/Search/Search.css";
import ClipLoader from "react-spinners/ClipLoader";
import Movie_API from '../../'

class Home extends React.Component {
  state = {
    favorites: [],
    list: [],

}

onTermSubmit = async query => {
    const KEY = process.env.REACT_APP_API;
    if (query !== "") {


      const response = await Movie_API.get("/search/movie", {
        params: {
          api_key: KEY,
          language: "en-US",
          include_adult: "false",
          page: "1",
          query: query,
        },
      });

      setTimeout(() => {
        this.setState({
          list: response.data.results,
        });
      },0)

    } else {
      this.setState({
        list: [],
      });
    }
}


render(){
  return (
    <div>

        <div>
          <div>
            <Header />
          </div>

          <span
            style={{
              justifyContent: "center",
              display: "flex",
              flexDirection: "row",
            }}
          >
            <h1>The Movie Hub</h1>
            <i
              style={{ marginLeft: "10px", marginTop: "15px" }}
              className="fas fa-film fa-3x"
            ></i>
          </span>

          <Search onFormSubmit = {this.onTermSubmit} />
          <div className="filter">
            <Link to={"/filter"}>
              <button className="filter-button">Discover One!</button>
            </Link>
          </div>

          <Logo />
        </div>

    </div>
  );
          }
};

export default Home;
