import "./Movies.css";
import MovieCard from "./MovieCard";
import Favorite from "./Favorite";
import ClipLoader from "react-spinners/ClipLoader";


const MovieList = ({movies}) => {
  const [favorite, setFavorite] = useState([])
  const arr = []

  const handleFavs = (props) => {

      props.map((index, key) => {
        if(arr.includes(...props)){
          arr.splice(key, 1)
        }else{
          arr.push(...props)
        }
      })

    console.log(arr)



}


 if(movies.length === 0){
   return(<div style={{display:'flex', justifyContent:'center'}}><h4>No results</h4> </div>)
 }
    const renderedList = movies
      .filter((movie) => movie.poster_path !== null)
      .map((movie, key) => {

        return (
          <div
            key={key}
            style={{ display: "inline-flex", flexDirection: "column" }}
          >
            <MovieCard
              image={`https://image.tmdb.org/t/p/w185${movie.poster_path}`}
              id={movie.id}
              title={movie.title}

            >
              <Favorite id={movie.id} handleFav={handleFavs}/>
            </MovieCard>





          </div>
        );
      });

    return (
      <div style={{ textAlign: "center" }}>
        <div className="movie-rendered-list">{renderedList}</div>
      </div>
    );
  }


export default MovieList;
