import React, { useState, useEffect } from "react";
import Search from "../../components/Search";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Logo from "../../components/TMDB-Image";
import "../../components/Search/Search.css";
import ClipLoader from "react-spinners/ClipLoader";
import Movie_API from '../../api/Movie_API'
import MovieList from '../../components/Movies/MovieList'

const Home = () => {
  const [list, getList] = useState([]);
  const [query, setQuery] = useState("");
  const [movies, getMovies] = useState([])
  const [loading, setLoading] = useState(true)
  const KEY = process.env.REACT_APP_API;


  useEffect(() => {
    const fetchData = async () => {
      if(query === ""){
        getList([])
      }else{
      const response = await Movie_API.get("/search/movie", {
        params: {
          api_key: KEY,
          language: "en-US",
          include_adult: "false",
          page: "1",
          query: query,
        },
      });
      getList(response.data.results)
    }

    }
    fetchData();
  }, [query])

  useEffect(() => {
    setTimeout(() => {
      setLoading(false)
    }, 1000)
  })


  const onTermSubmit = async (q) => {
      setQuery(q)

  };

  const getMovieLists = (props) => {


  }


  return (
    <>

        <>


          <span
            style={{
              justifyContent: "center",
              display: "flex",
              flexDirection: "row",
            }}
          >
            <h1>The Movie Hub</h1>
            <i
              style={{ marginLeft: "10px", marginTop: "15px" }}
              className="fas fa-film fa-3x"
            ></i>
          </span>

          <Search onFormSubmit={onTermSubmit} />
          <MovieList movies={list} getMovieList={getMovieLists}/>


          <div className="filter">
            <Link to={"/filter"}>
              <button className="filter-button">Discover One!</button>
            </Link>
          </div>

        </>

    </>
  );
};

export default Home;
