import React from 'react'
import './Search.css'
import Movie_API from '../../api/Movie_API'
import MovieCard from '../Movies/MovieCard'
import {Link} from 'react-router-dom'
import MovieInfo from '../../Pages/MovieInfo'
import SearchImg from '../../images/search.png'
import FavoriteIcon from '../../images/'

let response = ""
let data;
let dataLoop;
let firstImg;

class Search extends React.Component{



    state = {
        query: "",
        list: []
    }


    onHandleChange = (event) => {
        this.setState({
            query: event.target.value
        })
    }


    onTermSubmit = async (e) => {
        e.preventDefault()
        const KEY = process.env.REACT_APP_API

         response = await Movie_API.get('/search/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                query: this.state.query
            }
        })

        this.setState({
            list: response.data.results
        })



        console.log(this.state.list)


    }

    render(){
        const {list} = this.state
        return (
            <div>
                <form onSubmit={this.onTermSubmit}>
                    <input className="search" value={this.state.query} placeholder="Search For Any Movie" onChange={this.onHandleChange} type="text"></input>
                    <img src={SearchImg} style={{margin:"auto",display:"block",position:'relative',bottom:"30px",right:'350px' ,width:"20px"}}/>

                    {list
                    .filter(item => item.poster_path !== null)
                    .map(item => 
                        <div style={{display:'inline-block'}}>
                        <Link to={`/info/${item.id}/${encodeURIComponent(item.title)}`}>
                            <MovieCard image={`https://image.tmdb.org/t/p/w185${item.poster_path}`} key={item.id} title={item.title}/> 
                        </Link>
                        <button></button>
                         <button>Add to favorites</button>
                         </div>
                    )
                    }
                </form>




                     <Link to={'/filter'}>
                <button style={{margin:'50px auto 0 auto' ,display:'block', width:'150px', height:'50px'}}>or Discover One!</button>
                     </Link>
            </div>
        )
    }

}

export default Search
