import React from 'react'
import {Link} from 'react-router-dom'
import './Header.css'
import HeaderImg from '../../images/open-menu.png'

class Header extends React.Component{

    state = {
        clicked: true
    }

    onButtonClicked = () => {
        const { clicked } = this.state;
        this.setState({
            clicked: !clicked
        })
    }

    render(){
        return (
            <span>
                <input type="image" src={HeaderImg} style={{width:"30px", margin:"5px"}} onClick={this.onButtonClicked}/>
                <div className={`header-content${this.state.clicked ? '-fade-in' : '-fade-out'}`}>
                    <Link to="/list" style={{borderLeftWidth: '3px', borderLeftColor:'black', borderLeftStyle: 'dashed'}}>List</Link>
                    <Link to="/favorites">Favorites</Link>
                    <Link to="/" style={{borderRightWidth: '3px', borderRightColor:'black', borderLeftStyle: 'dashed'}}> Home</Link>

               </div>
            </span>
        )
    }

}

export default Header
