import React from 'react'
import {Link} from 'react-router-dom'
import './Header.css'
import HeaderImg from '../../images/open-menu.png'
import CloseImg from '../../images/close.png'


const imgPath = {
    open: HeaderImg,
    close: CloseImg
}
class Header extends React.Component{



    state = {
        clicked: true
    }

    onButtonClicked = () => {
        const { clicked } = this.state;
        this.setState({
            clicked: !clicked
        })
    }

    getImg = () => this.state.clicked ? 'close' : 'open'

    render(){
        const imgName = this.getImg()
        return (
            <span>
                    <img style={{width:'50px', cursor:'pointer', position:'relative', top:'10px'}} src={imgPath[imgName]} onClick={this.onButtonClicked} />
                <div className={`header-content${this.state.clicked ? '-fade-in' : '-fade-out'}`}>
                    <Link to="/list" style={{borderLeftWidth: '3px', borderLeftColor:'black', borderLeftStyle: 'dashed'}}>List</Link>
                    <Link to="/favorites">Favorites</Link>
                    <Link to="/"> Home</Link>
                    <Link to ='/signin' style={{borderRightWidth: '3px', borderRightColor:'black', borderRightStyle: 'dashed'}}> Sign-In</Link>
               </div>
            </span>
        )
    }

}

export default Header
