import React, { useState, useEffect } from "react";
import Search from "../../components/Search";
import { Link } from "react-router-dom";

import "../../components/Search/Search.css";
import {Movie_API} from '../../api/Movie_API'
import MovieList from '../../components/Movies/MovieList'
const KEY = process.env.REACT_APP_API;

const Home = () => {
  const [list, getList] = useState([]);
  const [query, setQuery] = useState("");
  //const [page, setPage] = useState(1) 

  /*
  const handlePage = () => {
    setPage(page + 1)
  }
  */


  useEffect(() => {
    const fetchData = async () => {
      if(query === ""){
        getList([])
      }else{
        try{
      const response = await Movie_API.Search.get("/search/movie", {
        params: {
          api_key: KEY,
          language: "en-US",
          include_adult: "false",
          page: "1",
          query: query,
        },
      });
      getList(response.data.results)
    }catch(err){
      console.warn(err)
      getList([])
    }
    }

    }
    fetchData();
  }, [query])



  const onTermSubmit = async (q) => {
      setQuery(q)

  };

  const getMovieLists = (props) => {


  }


  return (
    <>

        <>


          <span
            style={{
              justifyContent: "center",
              display: "flex",
              flexDirection: "row",
            }}
          >
            <h1>The Movie Hub</h1>
            <i
              style={{ marginLeft: "10px", marginTop: "15px" }}
              className="fas fa-film fa-3x"
            ></i>
          </span>

          <Search onFormSubmit={onTermSubmit} />

          <MovieList movies={list}/>



          <div className="filter">
            <Link to={"/filter"}>
              <button className="filter-button">Discover One!</button>
            </Link>
          </div>

        </>

    </>
  );
};

export default Home;
