import React from 'react'
import './Search.css'
import Movie_API from '../../api/Movie_API'
import MovieCard from '../Movies/MovieCard'
import {Link} from 'react-router-dom'
import MovieInfo from '../../Pages/MovieInfo'
import SearchImg from '../../images/search.png'
import FavoriteIcon from '../../images/favorite-icon.png'
import MovieList from '../Movies/MovieList'


let response = ""
let data;
let dataLoop;
let firstImg;

class Search extends React.Component{



    state = {
        query: "",
        page: 0,
        list: []
    }


    onHandleChange = (event) => {
        this.setState({
            query: event.target.value
        })
    }


    onTermSubmit = async (e) => {
        e.preventDefault()
        const KEY = process.env.REACT_APP_API
        if(this.state.query !== ""){
            response = await Movie_API.get('/search/movie', {
                params:{
                    api_key: KEY,
                    language: 'en-US',
                    include_adult: 'false',
                    page: '1',
                    query: this.state.query
                }
            })
            this.setState({
                list: response.data.results
            })
        }else{
            this.setState({
                list: []
            })

        console.log("Enter again")
        }








    }

    render(){
        const {list} = this.state
        return (
            <div style={{marginTop:'4%', display:'flex', flexDirection:'column'}}>
                <form onSubmit={this.onTermSubmit} className="center-search" /*style={{margin: "auto", maxWidth:'50%'}} */>
                    <input /*style={{width:'100%'}} */ className="search" value={this.state.query} placeholder="Search For Any Movie" onChange={this.onHandleChange} type="text"></input>
                    <i className="fas fa-search" /*style={{position:'relative', bottom:'30px', left:'10px'}} */></i>

                </form>
                <MovieList movies={this.state.list}/>

            </div>
        )
    }

}

export default Search
