import React, {useState} from 'react'
import './Movies.css'
import FavoriteIcon from '../../images/favorite-icon.png'
import {Link} from 'react-router-dom'
import MovieCard from './MovieCard'

const favoritePath = {
    favorite: 's',
    unfavorite: 'r'
}


const MovieList =  ({movies}) => {
    const [clicked, setClicked] = useState(false)
    const [movie, setMovie] = useState([])

    const handleClick = (e) => {
        let id = e.currentTarget.id
        setClicked(!clicked); 
        setMovie(movieFav => [...movieFav, id])
        console.log({movies})


    }

    if(movies.length === 0){
        return(
            <h3 style={{textAlign:'center'}}>No results</h3>
        )
    }else{

  const renderedList = movies
        .filter(movie => movie.poster_path !== null)
        .map((movie,key) => {
            return(
                <div>
                    <MovieCard image={`https://image.tmdb.org/t/p/w185${movie.poster_path}`} id={movie.id} title={movie.title}/> 
                    <div style={{width:'100%', display:'flex'}}>
                        <div style={{marginTop:'30px', display:'flex'}} key={key}>
                            <button  style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} onClick={handleClick} key={`${movie.id} favorite` }> 
                                    <i className={`fa${clicked ? 's' : 'r'} fa-heart fa-2x`}></i>
                            </button>
                            <button style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} id={movie.id}>
                                <i className="fas fa-list fa-2x" ></i>
                            </button>
                        </div>

                     </div>
                </div>
            )
        })




    return (
        <span>
            {renderedList}
        </span>  
    )
}
}

export default MovieList
