import React, { useState } from "react";


const Favorite = (props) => {
  let ls = window.localStorage
  let myVal = ls.getItem("fav")

  const [clicked, setClicked] = useState(false)
  const [movie, setMovie] = useState([])
  let array = movie;

  const handleClick = () => {
      let addArray = true;
      array.map((item,key) => {
        if(clicked === true){
          setMovie([...array])
          setClicked(true)
          props.handleFav({movie, clicked})
          addArray = false
        }
      })

      if(clicked === false){
      if(addArray){
        array.push(props.id)
      }else{
        array.map((item, key) => {
          array.splice(key,1)
        })
      }
      setMovie([...array])
      setClicked(!clicked)
      props.handleFav({movie, clicked})
    }






  }




  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}

    >


           <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={props.id}
        id={props.id}
      >





        <i
          className={`fa${clicked ? "s" : "r"} fa-heart fa-2x`}
          id={movie.id}
        ></i>
      </button>

      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        key={props.id}
        id={props.id}
      >
        <i className="fas fa-list fa-2x"></i>
      </button>



    </div>
  );

}


export default Favorite;
