import React, { useState, useEffect } from "react";
import Search from "../../components/Search";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Logo from "../../components/TMDB-Image";
import "../../components/Search/Search.css";
import ClipLoader from "react-spinners/ClipLoader";
import Movie_API from '../../api/Movie_API'
import MovieList from '../'

const Home = () => {
  const [list, getList] = useState([]);

  const onTermSubmit = async (q) => {
    const KEY = process.env.REACT_APP_API;
    if (q !== "") {


      const response = await Movie_API.get("/search/movie", {
        params: {
          api_key: KEY,
          language: "en-US",
          include_adult: "false",
          page: "1",
          query: q,
        },
      });

      getList(response.data.results)


    } else {
      getList([])
    }

  };



  return (
    <div>

        <div>
          <div>
            <Header />
          </div>

          <span
            style={{
              justifyContent: "center",
              display: "flex",
              flexDirection: "row",
            }}
          >
            <h1>The Movie Hub</h1>
            <i
              style={{ marginLeft: "10px", marginTop: "15px" }}
              className="fas fa-film fa-3x"
            ></i>
          </span>

          <Search onFormSubmit={onTermSubmit} />
          <MovieList movies={list} />
          <div className="filter">
            <Link to={"/filter"}>
              <button className="filter-button">Discover One!</button>
            </Link>
          </div>

          <Logo />
        </div>

    </div>
  );
};

export default Home;
