import React, {useState} from 'react'
import Search from '../../components/Search'
import Header from '../../components/Header'
import MovieCard from '../../components/Movies/MovieCard'
import Icon from '../../components/Icon'
import '../../components/Search/Search.css'
import Logo from '../../components/TMDB-Image'
import Movie_API from '../../api/Movie_API'
import Checkbox from './Checkbox'

let response;

const genre = [
    {
        name: "Action",
        id: 28
    },
    {
        name: 'Adventure',
        id: 12,
    },

    {
        name: "Animation" ,
        id: 16
    },

    {  
          name:"Comedy",
          id: 35
    },
    {
        name: "Crime",
        id: 80 
    },

    {
        name: "Documentary",
        id: 99
    },
    {

        name: "Drama",
        id: 18
    },
    {
        name: "Family",
        id: 10751
    },

    {
        name: "Fantasy",
        id: 14

    },

    {
        name: "History",
        id: 36
     },

     {
        name: "Horror",
        id: 27,
     },

     {
        name: "Music",
        id: 10402
     },

     {
        name: "Mystery",
        id: 9648
     },

     {
        name: "Romance",
        id: 10749,
     },

     {  
         name: "Science Fiction",
         id: 878
     },

     {
            name: "TV Movie",
            id: 10770
    },
    {
        name: "Thriller",
        id: 53
    },
    {
            name: "War",
            id: 10752
    },
    {
        name: "Western",
        id: 37
    }

]

console.log(genre)





function Filter () {
    const [checked, setChecked] = useState({})
    const [list, setList] = useState([])

    const handleClick = async (event) =>{ 
        setChecked({...checked, [event.target.name]: [event.target.id, event.target.checked]})
        const KEY = process.env.REACT_APP_API

        response =  await Movie_API.get('/discover/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                with_genres: `${event.target.id}`
            }
        })

       //setList(response.data.results)



    }

    const onTermSubmit = async (e) => {
        e.preventDefault()

          }



    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
                Search using these filters ⬇️
           </div>
           <div>
           <form onSubmit={onTermSubmit}>

                    {genre.map((item, key) => (
                        <div>

                                 <label style={{display:"block", margin:'10px'}}>{item.name}
                            <input type="checkbox" name={item.name} id={item.id} checked={checked[item.name.checked]} onChange={handleClick}/>


                        </label>



                    </div>

                    ))}
                    <button type="submit">Enter Here!</button>
               </form>



            </div>

            <Logo />



        </div>
    )
}

export default Filter
