import React from 'react'
import {Link} from 'react-router-dom'
import './Header.css'
import HeaderImg from '../../images/open-menu.png'
import CloseImg from '../../images/close.png'
import AvatarImg from '../../images/person-male.png'


const imgPath = {
    open: '-times-circle',
    close: '-bars'
}
class Header extends React.Component{



    state = {
        clicked: false
    }

    onButtonClicked = () => {
        const { clicked } = this.state;
        this.setState({
            clicked: !clicked
        })
    }

    getImg = () => this.state.clicked ? '-times-circle' : '-bars'

    render(){
        const imgName = this.getImg()
        return (
            <span style={{display:'inline-flex', flex}}>
                    <i class={`fas fa${imgName} fa-3x`} onClick={this.onButtonClicked} style={{cursor:'pointer'}}></i>
                <div className={`header-content${this.state.clicked ? '-fade-in' : '-fade-out'}`}>
                    <Link to="/list" /*style={{borderLeftWidth: '3px', borderLeftColor:'black', borderLeftStyle: 'dashed'}}*/>List</Link>
                    <Link to="/favorites">Favorites</Link>
                    <Link to="/"> Home</Link>
                    <Link to ='/signin' /*style={{borderRightWidth: '3px', borderRightColor:'black', borderRightStyle: 'dashed'}}*/> Sign-In</Link>
               </div>
               <img src={AvatarImg} style={{width:'50px', flexDirection}}/>
            </span>
        )
    }

}

export default Header
