import React from 'react'
import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import Home from '../Pages/Home'
import MovieInfo from '../Pages/MovieInfo'
import List from '../Pages/List'
import FavoritesList from '../Pages/FavoritesList'
import Filter from '../Pages/Filter'
import SignIn from '../Pages/Sign-In'
import MovieList from '../components/Movies/MovieList'
import Logo from '../components/TMDB-Image'
import Header from '../components/Header'
import ClipLoader from 'react-spinners'

let ls = window.localStorage
  if(ls.getItem("favoriteList") === null){
  let reset = ls.setItem("favoriteList", JSON.stringify([]))
  }else{
    JSON.parse(ls.getItem("favoriteList"))
  }

class App  extends React.Component{

  state = {
    loading: true
  }

  componentDidMount(){
    this.setState({
      loading: false
    })
  }

    render(){


        return(
          <div>
          {this.state.loading ? <ClipLoader size={150} color={"#123abc"} css={`
                    display: block;
                    margin: 0 auto;
                    border-color: black;
                  `} loading={this.state.loading}></ClipLoader> : (
            <Router>
            <Header />

            <div>
                    <Switch>
                        <Route exact path="/" component={Home}/>
                        <Route exact path="/info/:id/:name" component={MovieInfo} />
                        <Route exact path='/list' component={List} />
                        <Route exact path='/favorites' component={FavoritesList} />
                        <Route exact path='/filter' component={Filter} />
                        <Route exact path='/signin' component={SignIn} />
                    </Switch>
            </div>
            <Logo />
            </Router>
                  )
                  }
</div>
        )
                  }



}

export default App
