                    </div>
                    <p style={{display:'inline-block'}}>{info.overview === "" ? "No info here" : info.overview } </p>
                </div>


                </span>
                <img src={`https://image.tmdb.org/t/p/w185${castInfo.profile_path}`} style={{borderRadius:"50%", marginTop: '100px', marginBottom:'50px', width:'50%'}}></img>


                <Search />

                <Logo />

            </div>
        )
