import React, {useState} from 'react'

const Favorite = (props) => {
    const [clicked, setClicked] = useState(false)
    const [movie, setMovie] = useState([])

    const handleClick = (e) => {
        let id = e.currentTarget.id
        setClicked(!clicked); 
        setMovie(movieFav => [...movieFav, id])
        console.log(props)


    }

    return (
        <div style={{display:'inline-flex'}}>
            <button  style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} onClick={handleClick} key={`${props.id} favorite` } id={props.id}> 
                    <i className={`fa${clicked ? 's' : 'r'} fa-heart fa-2x`} id={movie.id}></i>
            </button>
            <button style={{backgroundColor:'transparent', border:'none', cursor:'pointer'}} key={`${props.id} list`} id={props.id}>
                <i className="fas fa-list fa-2x" ></i>
            </button>
        </div>

    )
}

export default Favorite
