      </button>):(<button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={`${props.id} favorite`}
        id={props.id}
      >
        <i
          className="far fa-heart fa-2x"
          id={movie.id}
        ></i>
      </button>
)


