import React from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'
import {withFirebase} from '../firebase'

let arr = JSON.parse(localStorage.getItem("favoriteList")) || []

class MovieCard extends React.Component {

  constructor(props){
    super(props)
  }


  state = {
    favorite: false
  }


handleFavs = (props) => {
  const found = arr.some(el => el.id === props.movie[0].id)
  if (!found){
    this.setState({
      favorite: true
    })
    arr.push({id: props.movie[0].id, title: props.movie[0].title, poster_path: props.movie[0].poster_path, favorite: true, release_date: props.movie[0].release_date})
  }
  else{
    const index = arr.findIndex(x => x.id === props.movie[0].id)
    this.setState({
      favorite: false
    })
    arr.splice(index, 1)
  }
  /*
  if(arr.includes(...props.movie)){
    const fav = arr.indexOf(...props.movie)
    arr.splice(fav, 1)
    localStorage.setItem("favoriteList", JSON.stringify(arr))
  }else{
    props.movie.map((index, key) => {
        arr.push(...props.movie)
        this.setState({
          favorites: arr
        })
      }
    )
  }
  */
 localStorage.setItem("favoriteList", JSON.stringify(arr))

}



render(){

  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${this.props.id}/${encodeURIComponent(this.props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>


      </div>


         <Favorite key={this.props.id} id={this.props.id} title={this.props.title} release_date={this.props.release_date} img={this.props.image} handleFav={this.handleFavs}/>













    </div>
  );
}
};

export default MovieCard;
