import React from 'react'
import moment from 'moment'
import { withAuthorization } from '../../session'
import {withFirebase} from '../../'


import MovieList from '../../components/Movies/MovieList'

const FavoritesList = (props) => {
    const date = moment(new Date()).format("YYYY-MM-DD")
    let newArr = []

    this.props.firebase.item(props.userID).on("child_added", snap => {

        if(Object.values(snap.val()).includes(this.props.id)){
           newArr.push(snap.val().is)

        }
      })
      console.log(newArr)

    let list = JSON.parse(localStorage.getItem("favoriteList"))

    const soon = list.filter(l => l.release_date > date)
    const already_released = list.filter(r => r.release_date < date)

    return (
        <div>


            <h3>My Favorites</h3>
            {list.length === 0 ? <p>Go back and add some favorites</p> : 
                <>
                {already_released.length !== 0 ? (<><h3>Past Releases</h3>
                <MovieList movies={already_released} isLoggedIn={props.loggedin} userID={props.user}/> </>) : null}

                 {soon.length !== 0 ? (<><h3>Future Releases</h3>
                <MovieList movies={soon} isLoggedIn={props.loggedin} userID={props.user}/> </>) : null}   

                </>
            }

        </div>
    )
}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(FavoritesList);
