import React from "react";
import { Link } from "react-router-dom";
import "./Icon.css";

class Icon extends React.Component {
  constructor(props){
    super(props)
  }
  render() {
    return (
      <>

       <Link
        to={this.props.prop ? "/profile" : '/signin'} /*style={{borderRightWidth: '3px', borderRightColor:'black', borderRightStyle: 'dashed'}}*/
      >
        {this.props.user !== "" ? <span style={{borderRadius: "50%", width: "50px", position: "absolute", right: "0%" , fontSize:'200%'}}>{this.props.user.toUpperCase()}</span> :   <i
          className="far fa-user fa-3x"
          style={{ width: "50px", position: "absolute", right: "0%" }}
        ></i>}


    </Link>

    </>

    );
  }
}

export default Icon;
