import React from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect, withRouter} from 'react-router-dom';
import Home from '../Pages/Home'
import MovieInfo from '../Pages/MovieInfo'
import List from '../Pages/List'
import FavoritesList from '../Pages/FavoritesList'
import Filter from '../Pages/Filter'
import SignIn from '../Pages/Sign-In'
import SignUpPage from '../Pages/Sign-Up'
import Logo from '../components/TMDB-Image'
import Header from '../components/Header'
import Error from '../Pages/Error'
import 'firebase/auth'
import {compose} from 'recompose'
import {withFirebase} from './firebase'
import Profile from '../Pages/Profile'


let ls = window.localStorage
  if(ls.getItem("favoriteList") === null){
     ls.setItem("favoriteList", JSON.stringify([]))
  }else{
    JSON.parse(ls.getItem("favoriteList"))
  }

  const AppPage = () => (
    <div>
      <AppForm/>
    </div>
  )

class App  extends React.Component{
  constructor(props){
    super(props)

    this.state = {
      loggedIn: null,
      user: null
    }

  }

  componentDidMount(){
   this.props.firebase.auth.onAuthStateChanged(user => {
      if(user){
        console.log("user is logged in")
        this.setState({loggedIn: true})


      }else{
        console.log("user is logged out")
        console.log(user)
        this.setState({loggedIn: false})

       }
    })
  }

    render(){

        return (

            <Router>
            <Header />

            <div>
                    <Switch>
                        <Route exact path="/" component={Home}/>
                        <Route exact path="/info/:id/:name" component={MovieInfo} />
                        <Route exact path='/list' component={List} />
                        <Route exact path='/profile' component={Profile} />
                        <Route exact path='/favorites' component={FavoritesList} />
                        <Route exact path='/filter' component={Filter} />
                        <Route exact path='/signin' component={Signin} />
                        <Route exact path='/filter' component={Filter} />

                        <Route exact path='/signin'  render={() => (!this.state.loggedIn ? <div><Redirect to= "/signin" /> </div>: <Redirect to= "/profile" />)}/>
                        <Route exact path='/signup' render={() => (!this.state.loggedIn ? <SignUpPage/> : <Redirect to= "/profile" />)}/>
                        <Route component={Error} />
                        {console.log(!this.state.loggedIn)}
                    </Switch>
            </div>
            <Logo />
            </Router>

        )


    }
}

const AppForm = compose(
  withRouter,
  withFirebase
)(App)

export default AppPage
