import React, {useState} from 'react'
import Header from '../../components/Header'
import MovieList from '../../components/Movies/MovieList'
import Icon from '../../components/Icon'
import '../../components/Search/Search.css'
import Logo from '../../components/TMDB-Image'
import Movie_API from '../../api/Movie_API'

let response;

const genre = [
    {
        name: "Action",
        id: 28
    },
    {
        name: 'Adventure',
        id: 12,
    },

    {
        name: "Animation" ,
        id: 16
    },

    {  
          name:"Comedy",
          id: 35
    },
    {
        name: "Crime",
        id: 80 
    },

    {
        name: "Documentary",
        id: 99
    },
    {

        name: "Drama",
        id: 18
    },
    {
        name: "Family",
        id: 10751
    },

    {
        name: "Fantasy",
        id: 14

    },

    {
        name: "History",
        id: 36
     },

     {
        name: "Horror",
        id: 27,
     },

     {
        name: "Music",
        id: 10402
     },

     {
        name: "Mystery",
        id: 9648
     },

     {
        name: "Romance",
        id: 10749,
     },

     {  
         name: "Science Fiction",
         id: 878
     },

     {
            name: "TV Movie",
            id: 10770
    },
    {
        name: "Thriller",
        id: 53
    },
    {
            name: "War",
            id: 10752
    },
    {
        name: "Western",
        id: 37
    }

]






function Filter () {
    const [checked, setChecked] = useState({})
    const [id, setID] = useState([])
    const [list, setList] = useState([])

    const handleClick = async (event) =>{ 
        setChecked({[event.target.name]: [event.target.checked]})
        setID(id.concat(event.target.id))



       //setList(response.data.results)



    }

    const onTermSubmit = async (e) => {
        const KEY = process.env.REACT_APP_API


        let getAllID = [...id];

        e.preventDefault()
        response =  await Movie_API.get('/discover/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                with_genres: id
            }
        })

        console.log(getAllID)
        setList(response.data.results)


          }



    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
                Search using these filters <span role='img' aria-label="down">⬇️</span>
           </div>
           <div>
           <form onSubmit={onTermSubmit}>

                    {genre.map((item, key) => (
                        <div key={key} style={{display:'inline-block'}}>

                                 <label key={key} style={{display:"block", margin:'10px'}}>{item.name}
                            <input key={key} type="checkbox" name={item.name} id={item.id} checked={(checked)[item.checked]}  onChange={handleClick}/>


                        </label>



                    </div>

                    ))}
                    <button type="submit">Enter Here!</button>
               </form>

              <MovieList movies={list} />




            </div>

            <Logo />



        </div>
    )
}

export default Filter
