import React, { useState } from "react";

let ls = window.localStorage
if(ls.getItem("fav") === null){
  let reset = ls.setItem("fav", JSON.stringify([]))
  }else{
    JSON.parse(ls.getItem("fav"))
  }

const Favorite = (props) => {

  const [clicked, setClicked] = useState(false)
  const [movie, setMovie] = useState([{id: 0, title: ''}])
  let array = movie;

  const handleClick = () => {
      let addArray = true;
      array.map((item,key) => {
        if(clicked === true){
          setMovie({id: array.id, title: array.title})
          props.handleFav({movie, clicked})
          addArray = false
        }
      })

      if(addArray){
        setMovie([{id: props.id, title: props.title}])
        console.log(array)
      }else{
        array.map((item, key) => {
          array.splice(key,1)
        })
      }
      setMovie([{id: array.id, title: array.title}])
      setClicked(!clicked)
      props.handleFav({movie, clicked})




  }




  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}

    >


           <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={props.id}
        id={props.id}
      >




        {JSON.parse(ls.getItem("fav").includes(props.id)) ? (<i
          className={`fas fa-heart fa-2x`}
          id={movie.id}
        ></i>) : <i
        className={`far fa-heart fa-2x`}
        id={movie.id}
      ></i>}

      </button>



     {/*  <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        key={props.id}
        id={props.id}
      >
        <i className="fas fa-list fa-2x"></i>
      </button> */}





    </div>
  );

}


export default Favorite;
