import React, { useState } from "react";


const Favorite = (props) => {
  const [clicked, setClicked] = useState(true)
  const [movie, setMovie] = useState([1])


  const handleClick = index => e => {
       let array = [...movie];
       array[index] = e.target.id
       console.log(array[index])
       setMovie(array)

  }


  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}
    >

{movie.map((index, key) => (
  <button
  style={{
    backgroundColor: "transparent",
    border: "none",
    cursor: "pointer",
  }}
  onClick={handleClick(key)}
  key={`${props.id} favorite`}
  id={index.id}
>
  <i
    className={`fa${clicked ? "s" : "r"} fa-heart fa-2x`}
    id={movie.id}
  ></i>
</button>
))}


      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        key={`${props.id} list`}
        id={props.id}
      >
        <i className="fas fa-list fa-2x"></i>
      </button>



    </div>
  );

}


export default Favorite;
