import React, {useState} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'

let arr = JSON.parse(localStorage.getItem("favoriteList")) || []

const MovieCard = (props) => {




  const [favorite, setFavorite] = useState(false)


const handleFavs = () => {
  const found = arr.some(el => el.id === props.movie[0].id)
  if (!found){
    setFavorite(true)
    arr.push({id: props.movie[0].id, title: props.movie[0].title, poster_path: props.movie[0].poster_path, favorite: favorite})
  }
  else{
    const index = arr.findIndex(x => x.id === props.movie[0].id)
    setFavorite(false)
    arr.splice(index, 1)
  }
  /*
  if(arr.includes(...props.movie)){
    const fav = arr.indexOf(...props.movie)
    arr.splice(fav, 1)
    localStorage.setItem("favoriteList", JSON.stringify(arr))
  }else{
    props.movie.map((index, key) => {
        arr.push(...props.movie)
        this.setState({
          favorites: arr
        })
      }
    )
  }
  */
 localStorage.setItem("favoriteList", JSON.stringify(arr))

}


  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${props.id}/${encodeURIComponent(props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>


      </div>

      <Favorite key={props.id} id={props.id} title={props.title} img={props.image} handleFav={handleFavs}/>










    </div>
  );
}


export default MovieCard;
