  const [movie, setMovie] = useState([])
  let array = movie;

  const handleClick = () => {


      if(props.isLoggedIn){
        array.push({id: props.id, title: props.title, poster_path: props.img.replace("https://image.tmdb.org/t/p/w185", ""), release_date: props.release_date})
        setMovie([...array])
        setClicked(!clicked)
        props.handleFav({movie, clicked})
      }else{
        alert("You need to sign in")
      }



  }







  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}

    >


           <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={props.id}
        id={props.id}
      >




        {props.loJSON.parse(ls.getItem("favoriteList").includes(props.id) && ls.getItem("favoriteList").includes(props.title)) ? (<i
          className={`fas fa-heart fa-2x`}
          id={movie.id}
        ></i>) : <i
        className={`far fa-heart fa-2x`}
        id={movie.id}
      ></i>}

      </button>



