const Favorite = (props) => {
  const [clicked, setClicked] = useState(true)
  const [movie, setMovie] = useState([])
  const [id, setID] = useState("")
  let array = movie;

  const handleClick = () => {
      setMovie(movie => [...movie, id])

  }


  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}
    >
           {console.log(movie)}


           <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={`${props.id} favorite`}
        id={props.id}
      >
        <i
          className={`fa${clicked ? "s" : "r"} fa-heart fa-2x`}
          id={movie.id}
        ></i>
      </button>
      </div>
  )

}


export default Favorite;
