import React, { useState, useEffect } from "react";
import "./Movies.scss";
import MovieCard from "./MovieCard";
import ClipLoader from "react-spinners/ClipLoader";

const MovieList = ({ movies, isLoggedIn, userID }) => {
  const [loading, setLoading] = useState(true);
  let renderedList;
  let cards = []

  const getNewCards = (key, movie) => {
    for(let i = 0; i < 5; i++){
      cards.push(<> 
       <div
            key={key}
            className="movie-list"
          >

          </div>
      </>)
    }
    return cards
  }

  useEffect(() => {
    let time = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => {
      clearTimeout(time);
      setLoading(true);
    };
  }, [movies]);

  if (movies.length === 0) {
    return (
      <div style={{ display: "flex", justifyContent: "center" }}>
        <h4>No results</h4>{" "}
      </div>
    );
  }

  if (loading) {
    return (
      <ClipLoader
        size={150}
        color={"#123abc"}
        css={`
          display: block;
          margin: 0 auto;
          border-color: black;
        `}
        loading={loading}
      ></ClipLoader>
    );
  } else {
    renderedList = movies
      .filter((movie , key) => movie.poster_path !== null)
      .map((movie, key) => {
        return (
          <>
          {getNewCards(key, movie)}
          </>
        );
      });
  }

  return (
    <div style={{ textAlign: "center" }}>
      <div className="movie-rendered-list">{renderedList}</div>
    </div>
  );
};

export default MovieList;
