import React from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import Home from '../Pages/Home'
import MovieInfo from '../Pages/MovieInfo'
import List from '../Pages/List'
import FavoritesList from '../Pages/FavoritesList'
import Filter from '../Pages/Filter'
import SignIn from '../Pages/Sign-In'
import SignUpPage from '../Pages/Sign-Up'
import Logo from '../components/TMDB-Image'
import Header from '../components/Header'
import Error from '../Pages/Error'
import {compose} from 'recompose'
import {withFirebase} from './firebase'
import Profile from '../Pages/Profile'


  const AppPage = () => (
    <div>
      <AppForm/>
    </div>
  )

class App  extends React.Component{
  constructor(props){
    super(props)

    this.state = {
      loggedIn: null,
      user: null,
      userChar: '',
      userEmail: '',
      favorites: []
    }

  }

  componentDidMount(){
   this.props.firebase.auth.onAuthStateChanged(user => {

      if(user){
        this.setState({loggedIn: true, user: user.uid, userEmail: user.email})
        console.log(this.state.userEmail.substring(0, this.state.user))
        if(!user.isAnonymous){
          this.setState({
            userChar: user.email.charAt(0).toUpperCase()

          })
        }else{
          this.setState({
            userChar: "An"
          })
        }


      }else{
        this.setState({loggedIn: false, user: "", userChar: ""})


       }
    })
  }

    render(){

        return (


            <Router>
            <Header loggedin = {this.state.loggedIn} user = {this.state.userChar}/>

            <div>
                    <Switch>
                        <Route exact path="/" render={(props) => <Home {...props} loggedin = {this.state.loggedIn} user={this.state.user}/> }/>
                        <Route exact path="/info/:id/:name" render={(props) => <MovieInfo {...props} loggedin = {this.state.loggedIn} user={this.state.user}/> } />
                        <Route exact path='/list' component={List} />
                        <Route exact path='/profile' render={(props) => <Profile {...props} user = {this.state.user}/> } />
                        <Route exact path='/favorites' render={(props) => <FavoritesList {...props} loggedin = {this.state.loggedIn} user={this.state.user}/> }/>
                        <Route exact path='/filter' render={(props) => <Filter {...props} loggedin = {this.state.loggedIn} user={this.state.user}/> } />
                        <Route exact path='/signin' component={SignIn} />
                        <Route exact path='/signup' component={SignUpPage} />

                        <Route exact path='/signin'  render={() => (this.state.loggedIn ? <div><Redirect to= "/signin" /> </div>: <Redirect to= "/profile" />)}/>
                        <Route exact path='/signup' render={() => (this.state.loggedIn ? <SignUpPage/> : <Redirect to= "/profile" />)}/>
                        <Route component={Error} />
                    </Switch>
            </div>
            <Logo />
            </Router>


        )


    }
}

const AppForm = compose(
  withFirebase
)(App)

export default AppPage
