import React, { useState } from "react";


const Favotite = (props) => {
  const [clicked, setClicked] = useState(false)
  const [movie, setMovie] = useState([])

  const handleClick = () => {
      let array = movie;
      let addArray = true;
      array.map((item,key) => {
        if(item === props.id){
          array.splice(key,1)
          addArray = false
        }
      })

      if(addArray){
        array.push(props.id)
      }
      setMovie([...array])
  }


  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}
    >
      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={`${props.id} favorite`}
        id={props.id}
      >
        <i
          className={`fa${clicked ? "s" : "r"} fa-heart fa-2x`}
          id={movie.id}
        ></i>
      </button>
      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        key={`${props.id} list`}
        id={props.id}
      >
        <i className="fas fa-list fa-2x"></i>
      </button>


    </div>
  );
}


export default Favotite;
