import React, {useState, useEffect} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'
import FavoritesList from "../../Pages/FavoritesList";

let arr = JSON.parse(localStorage.getItem("favoriteList")) || []

class MovieCard extends React.Component {



  constructor(props){
    super(props)   
  }
  state = {
    favorites: JSON.parse(localStorage.getItem("favoriteList")) || []
  }




handleFavs = (props) => {


  if(arr.includes(props.movie[0])){
    const fav = arr.indexOf(...props.movie)
    arr.splice(fav, 1)
    localStorage.setItem("favoriteList", JSON.stringify(arr))
  }else{
    props.movie.map((index, key) => {
        arr.push(...props.movie)
        this.setState({
          favorites: arr
        })
        localStorage.setItem("favoriteList", JSON.stringify(arr))
      }
    )
  }
}

componentDidMount(){
  if(arr.length !== 0)
    arr.map((index, key) => {

      console.log(arr[key].id)
    })
}


render(){



  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${this.props.id}/${encodeURIComponent(this.props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>


      </div>

      <Favorite key={this.props.id} id={this.props.id} title={this.props.title} poster_path={this.props.image} handleFav={this.handleFavs}/>









    </div>
  );
}
};

export default MovieCard;
