import React, {useState} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'

let arr = []

class MovieCard extends React.Component {



  constructor(props){
    super(props)
    this.state = {
      favorites: [0],
      items: [this.props.id]
  }

  this.handleFavs = this.handleFavs.bind(this)


  }


handleFavs = (props) => {

  const index2 = arr.indexOf(...props.movie)

    props.movie.map((index, key) => {
      if(arr.includes(...props.movie) || props.clicked === true){
        arr.splice(index2, 1)
      }else{
        arr.push(...props.movie)
      }
    })


    this.setState({
      favorites: arr, ...this.state.favorites
    })

}

componentWillReceiveProps(){
  this.setState({
    items: [this.props.id]
  })
}

componentDidMount(){
  if(this.state.favorites.includes(this.props.id)){
    const favorites = this.state.favorites.indexOf(this.props.id)
    this.state.favorites.splice(favorites, 1)
    console.log(3)
  }

  this.setState({
    items: [this.props.id],
    favorites: localStorage.setItem("fav", JSON.stringify(this.state.favorites))


  })

}


render(){


  const {children} = this.props

  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${this.props.id}/${encodeURIComponent(this.props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>

        <Favorite key={this.props.id} id={this.props.id} handleFav={this.handleFavs}/>

      </div>



      {React.Children.map(children, (child, i) => {
        return child
      })}



    </div>
  );
}
};

export default MovieCard;
