import React, {useState, useEffect} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'

const MovieCard = ({movies}) => {
  let ls = window.localStorage

  const [favorite, setFavorite] = useState(JSON.parse(ls.getItem("fav")) || [])

  useEffect(() => {
    ls.setItem("fav", JSON.stringify(favorite))
  })

  const arr = []
  const arr2 = []
  let myVal = ls.getItem("fav")

  const handleFavs = (fav) => {
    if(favorite.includes(...fav.movie)){
      const favorites = favorite.indexOf(...fav.movie)
      favorite.splice(favorites, 1)
      console.log(3)
    }else{
    const index2 = arr.indexOf(...fav.movie)

      fav.movie.map((index, key) => {
        if(arr.includes(...fav.movie) || fav.clicked === true){
          arr.splice(index2, 1)
        }else{
          arr.push(...fav.movie)
        }
      })
    }

      setFavorite(arr.concat(...favorite))
  }




  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${this.props.id}/${encodeURIComponent(this.props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>
        <Favorite key={this.props.id} id={this.props.id} handleFav={handleFavs}/>



      </div>


      {React.Children.map(children, (child, i) => {
        return child
      })}



    </div>
  );

};

export default MovieCard;
