import React, {useState} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'

let arr = JSON.parse(localStorage.getItem("fav")) || []

class MovieCard extends React.Component {



  constructor(props){
    super(props)   
  }
  state = {
    favorites: JSON.parse(localStorage.getItem("fav")) || []
  }


handleFavs = (props) => {
  arr.map((index, key) => {
    if(arr.includes(props.movie[key])){
      const fav = arr.indexOf(props.movie[key])
      arr.splice(fav, 1)
      localStorage.setItem("fav", JSON.stringify(arr))
    }
  else{
    props.movie.map((index, key) => {
        arr.push(...props.movie)
        this.setState({
          favorites: arr
        })
        localStorage.setItem("fav", JSON.stringify(arr))
      }
    )
  }
}
  )}



render(){



  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${this.props.id}/${encodeURIComponent(this.props.title)}`}>
          <img src={this.props.image} style={{ width: "100%" }} alt="Movie" />

        </Link>


      </div>

      <Favorite key={this.props.id} id={this.props.id} title={`${encodeURIComponent(this.props.title)}`} handleFav={this.handleFavs}/>









    </div>
  );
}
};

export default MovieCard;
