]

console.log(genre)





const Filter = () => {
    const [checked, setChecked] = useState({})
    const [list, setList] = useState()

    const handleClick = (event) =>{ 
        setChecked({...checked, [event.target.name]: event.target.checked})
        console.log(event.target.id)

    }

    const submitButton =  (event) => {
        const KEY = process.env.REACT_APP_API

         response =  Movie_API.get('/discover/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                with_genres: `${event.target.id}%2C%20`
            }
        })

        console.log("clicked!")


    }




    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
                Search using these filters ⬇️
           </div>
           <div>
            <form>
                    {genre.map((item, key) => (
                        <div>
                        <label style={{display:"block", margin:'10px'}}>{item.name}
                            <input type="checkbox" name={item.name} id={item.id} checked={checked[item.name]} onChange={handleClick}/>
                        </label>

                    </div>

                    ))}



            </form>
            </div>

            <Logo />



        </div>
    )
}

export default Filter
