import React, {useState} from 'react'
import Search from '../../components/Search'
import Header from '../../components/Header'
import MovieCard from '../../components/Movies/MovieCard'
import Icon from '../../components/Icon'
import '../../components/Search/Search.css'
import Logo from '../../components/TMDB-Image'
import Movie_API from '../../api/Movie_API'
import Checkbox from './Checkbox'

let response;

const genre = [
    {
        name: "Action",
        id: 28
    },
    {
        name: 'Adventure',
        id: 12,
    },

    {
        name: "Animation" ,
        id: 16
    },

    {  
          name:"Comedy",
          id: 35
    },
    {
        name: "Crime",
        id: 80 
    },

    {
        name: "Documentary",
        id: 99
    },
    {

        name: "Drama",
        id: 18
    },
    {
        name: "Family",
        id: 10751
    },

    {
        name: "Fantasy",
        id: 14

    },

    {
        name: "History",
        id: 36
     },

     {
        name: "Horror",
        id: 27,
     },

     {
        name: "Music",
        id: 10402
     },

     {
        name: "Mystery",
        id: 9648
     },

     {
        name: "Romance",
        id: 10749,
     },

     {  
         name: "Science Fiction",
         id: 878
     },

     {
            name: "TV Movie",
            id: 10770
    },
    {
        name: "Thriller",
        id: 53
    },
    {
            name: "War",
            id: 10752
    },
    {
        name: "Western",
        id: 37
    }

]

console.log(genre)



const submitButton = async (event) => {
        const KEY = process.env.REACT_APP_API

         response = await Movie_API.get('/discover/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                with_genres: `${event.target.id}%2C%20`
            }
        })

        console.log(response)


}


const Filter = () => {
    const [checked, setChecked] = useState({})

    const handleClick = (event) =>{ 
        setChecked({...checked, [event.target.name]: event.target.checked})
        console.log("checked items: " , checked)

    }



    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
                Search using these filters ⬇️
           </div>
           <div>
            <form>
                    {genre.map((item, key) => (
                        <div>
                        <label style={{display:"block", margin:'10px'}}>{item.name}
                            <input type="checkbox" key={key} name={item.name} id={item.id} style={{margin: "0 5px 0 10px"}} onClick={handleClick} checked={checked[item.name]}/>
                        </label>

                    </div>

                    ))}



            </form>
            </div>

            <Logo />



        </div>
    )
}

export default Filter
