     function MovieInfo({match}) {
        useEffect(() => {
            const fetchInfo = async () => {
                const fetchInfo = await Movie_API.get(`/movie/${match.params.id}`,{
                    params:{
                        api_key: process.env.REACT_APP_API,
                        language: 'en-US'
                    }
                });
                const info = await fetchInfo.data

                setInfo(info)
                window.scrollTo(0, 0)

            };
            fetchInfo();
        }, [match]);



        const [info, setInfo] = useState({});




        return (


            <div>
            <Header />
            <div className="title">
                { info.title !== info.original_title ? <h2> {`${info.title} (${info.original_title})`}</h2> : <h2 style={{display:'inline-block'}}> {info.original_title}</h2>}
                 <h2 style={{display:'inline-block', marginLeft:'10px'}}>({info.release_date})</h2>
            </div>
            <div style={{marginLeft:'20px'}}>
                <div className="movie">
                    <img src={`https://image.tmdb.org/t/p/w185${info.poster_path}`} className="movie-img" alt="Movie title"/>
                </div>
                <div>
                    <h3 style={{textAlign:'left'}}>{info.vote_average}/10</h3>
                </div>
                <div className="description">
                    <p style={{display:'inline-block'}}>{info.overview === "" ? "No info here" : info.overview } </p>
                </div>
            </div>

            <div style={{marginTop:"550px"}}>
            <h2 style={{display: 'inline', position:'relative', bottom:'200px', left:'600px'}}>Continue Searching Below <span role="img" aria-label="down">⬇️</span> </h2>

                <Search  />
            </div>

            <Logo />

            </div>
        )
    }




export default MovieInfo
