import React, {useState, useEffect} from 'react'
import Search from '../../components/Search'
import Header from '../../components/Header'
import '../../components/Search/Search.css'
import Movie_API from '../../api/Movie_API'
import MovieList from '../../components/Movies/MovieList'
import '../styles.css'
import Logo from '../../components/TMDB-Image'
import Favorites from '../../components/Movies/Favorite'
import { OverlayTrigger, Tooltip } from 'react-bootstrap'


/*
const MovieInfo = () => {

    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
               jkjkjkjkj
           </div>
           <div style={{textAlign:"center", position:'relative', top:'100px'}}>
               <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. </p>
           </div>
           <div style={{marginTop:"550px"}}>
                <Search  />
           </div>
        </div>
    )
}
*/



     function MovieInfo({match}) {
        useEffect(() => {
            const fetchInfo = async () => {
                const fetchInfo = await Movie_API.get(`/movie/${match.params.id}`,{
                    params:{
                        api_key: process.env.REACT_APP_API,
                        language: 'en-US'
                    }
                });

                const info = await fetchInfo.data

                setInfo(info)
                window.scrollTo(0, 0)

            };
            fetchInfo();
        }, [match]);

        useEffect(() => {
            const fetchCast = async () => {
                const fetchCast = await Movie_API.get(`/movie/${match.params.id}/credits`,{
                    params:{
                        api_key: process.env.REACT_APP_API,
                    }
                });

                const castInfo = await fetchCast.data.cast

                setCastInfo(castInfo)

                window.scrollTo(0, 0)

            };
            fetchCast();
        }, [match]);

        useEffect(() => {
            const fetchCrew = async () => {
                const fetchCrew = await Movie_API.get(`/movie/${match.params.id}/credits`,{
                    params:{
                        api_key: process.env.REACT_APP_API,
                    }
                });

                const crewInfo = await fetchCrew.data.crew

                setCrewInfo(crewInfo)

                window.scrollTo(0, 0)

            };
            fetchCrew();
        }, [match]);

        useEffect(() => {
            const fetchRecommend = async () => {
                const fetchRecommend = await Movie_API.get(`/movie/${match.params.id}/recommendations`,{
                    params:{
                        api_key: process.env.REACT_APP_API,
                    }
                });

                const recommendInfo = await fetchRecommend.data.results

                setRecInfo(recommendInfo)
                window.scrollTo(0, 0)

            };
            fetchRecommend();
        }, [match]);



        const [info, setInfo] = useState({});
        const [castInfo, setCastInfo] = useState([]);
        const [crewInfo, setCrewInfo] = useState([]);
        const [recommendInfo, setRecInfo] = useState([]);





        return (

            <div>
                <Header/>
                <span className="info">

                <span style={{display:'flex', flexDirection:'column'}}>
                    <span className="title">

                     </span>
                 <img src={`https://image.tmdb.org/t/p/w185${info.poster_path}`} alt="Movie Title" />

                        <Favorites />

                </span>
                <div className="description">
                    <span style={{display:'-webkit-flex', WebkitFlexDirection:'row', justifyContent:'center'}}>
                            { info.title !== info.original_title ? <h2> {`${info.title} [${info.original_title}]`}</h2> : <h2> {info.original_title}</h2>}

                             <h2 style={{marginLeft:'10px'}}>({info.release_date})</h2>
                    </span>
                    <div style={{display: 'grid', gridTemplateColumns: 'auto auto auto'}}>
                        <h4>Rating: {info.vote_average}/10</h4>
                        <h4>Voter Count: {info.vote_count}</h4>
                        <h4>Runtime: {info.runtime} minutes</h4>
                    </div>
                    <p style={{display:'inline-block'}}>{info.overview === "" ? "No info here" : info.overview } </p>
                </div>


                </span>

                <div style={{width:'100%', height:'0px', border:'2px black solid', marginTop:'3em'}}></div>

                <div style={{display:'flex', flexDirection:'row', overflowX:'scroll',overflowY:'hidden', marginTop:'20px', marginBottom:'90px'}}>
                {castInfo
                .map((cast,key) => (
                    <OverlayTrigger
                        key={key}
                        placement="bottom"
                        overlay = {
                            <Tooltip key={key} style={{paddingTop:'20px', textAlign:'center', justifyContent:'center'}}>{cast.character}({cast.name})</Tooltip>
                        }
                        >

                            <div style={{width:'100px', height:'122px', marginRight:'50px'}}>
                                 <img key={key} alt={cast.name} src={
                                     cast.profile_path === null ? 'https://previews.123rf.com/images/urfandadashov/urfandadashov1805/urfandadashov180500070/100957966-photo-not-available-icon-isolated-on-white-background-vector-illustration.jpg' : `https://image.tmdb.org/t/p/w185${cast.profile_path}`} 
                                     style={{ borderRadius:"100%",width:'95px', height:'113px', padding:"5px"}}/>
                             </div>

                        </OverlayTrigger>
                ))}
                  </div>

                  <div style={{display:'flex', flexDirection:'row', overflowX:'scroll',overflowY:'hidden', marginTop:'20px', marginBottom:'90px'}}>

                  {crewInfo
                .map((crew,key) => (
                    <OverlayTrigger
                        key={key}
                        placement="bottom"
                        overlay = {
                            <Tooltip key={key} style={{paddingTop:'20px', textAlign:'center'}}>{crew.name} ({crew.job})</Tooltip>
                        }
                        >

                            <div style={{width:'100px', height:'122px', marginRight:'50px'}}>
                                 <img key={key} alt={crew.name} src=
                                    {crew.profile_path === null ? 'https://previews.123rf.com/images/urfandadashov/urfandadashov1805/urfandadashov180500070/100957966-photo-not-available-icon-isolated-on-white-background-vector-illustration.jpg' : `https://image.tmdb.org/t/p/w185${crew.profile_path}`} 
                                    style={{ borderRadius:"50%",width:'95px', height:'113px', padding:"5px"}}/>
                             </div>

                        </OverlayTrigger>
                ))}
                  </div>

                  <div style={{width:'100%', height:'0px', border:'2px black solid',marginTop:'3em'}}></div>


                <div style={{textAlign:'center'}}>
                    <h3>Recommendations</h3>
                </div>


                <MovieList movies={recommendInfo}/>

                <div style={{width:'100%', height:'0px', border:'2px black solid',marginTop:'3em'}}></div>





                <Search />

                <Logo />

            </div>
        )
    }




export default MovieInfo
