import React, { useState } from "react";

const Favorite = (props) => {
  const [clicked, setClicked] = useState(false);
  const [movie, setMovie] = useState([]);

  const handleClick = (event) => {

    setClicked(!clicked);
    if (clicked === true) {
      setMovie(movie.concat(event.target.id));
    } else {
      let index = movie.indexOf(event.target.id);
      if (index > -1) {
        movie.splice(index, 1);
      }
    }
  }


  return (
    <div
      style={{
        display: "inline-flex",
        marginTop: "20px",
        justifyContent: "center",
      }}
    >
      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        onClick={handleClick}
        key={`${props.id} favorite`}
        id={props.id}
      >
        <i
          className={`fa${clicked ? "s" : "r"} fa-heart fa-2x`}
          id={movie.id}
        ></i>
      </button>
      <button
        style={{
          backgroundColor: "transparent",
          border: "none",
          cursor: "pointer",
        }}
        key={`${props.id} list`}
        id={props.id}
      >
        <i className="fas fa-list fa-2x"></i>
      </button>

      {console.log(movie)}
    </div>
  );
};

export default Favorite
