import React, {useState} from 'react'
import './Movies.css'
import FavoriteIcon from '../../images/favorite-icon.png'
import {Link} from 'react-router-dom'
import MovieCard from './MovieCard'



const MovieList =  ({movies}) => {
    const [clicked, setClicked] = useState(false)
    const [movie, setMovie] = useState([])

    const handleClick = (e) => {
        let id = e.currentTarget.id
        setClicked(!clicked); 
        setMovie(movieFav => [...movieFav, id])


    }

    if(movies.length === 0){
        return(
            <h3 style={{textAlign:'center'}}>No results</h3>
        )
    }else{

  const renderedList = movies
        .filter(movie => movie.poster_path !== null)
        .map((movie) => {
            return(
                <div style={{display:'inline-flex'}}>
                <MovieCard image={`https://image.tmdb.org/t/p/w185${movie.poster_path}`} id={movie.id} title={movie.title}/> 

            </div>

            )
        })




    return (
        <span>
            {renderedList}
        </span>  
    )
}
}

export default MovieList
