import React, {useState} from "react";
import "./Movies.css";
import { Link } from "react-router-dom";
import Favorite from './Favorite'

const MovieCard = (props) => {

  state = {
    favorites: [0]
}

handleFav = (fav) => {
    this.setState({
        favorites: [...this.state.favorites, fav]
    })

}

  return (
    <div className="movie-list">
      <div
        className=" movie-info" /*style={{display:'flex',flexDirection:'column-reverse'}} */
      >
        <Link to={`/info/${props.id}/${encodeURIComponent(props.title)}`}>
          <img src={props.image} style={{ width: "100%" }} alt="Movie" />
        </Link>

      </div>
      <Favorite id={props.id} />
      {console.log(props)}
    </div>
  );
};

export default MovieCard;
