import React from 'react'
import {Link} from 'react-router-dom'
import './Header.css'
import HeaderImg from '../../images/open-menu.png'

class Header extends React.Component{

    state = {
        clicked: 'fade-in'
    }

    onButtonClicked = () => {
        const { clicked } = this.state;
        this.setState({
            clicked: !clicked
        })
    }

    render(){
        return (
            <span style={{display:"inline"}}>
                <input type="image" src={HeaderImg} style={{width:"30px", margin:"10px"}} onClick={this.onButtonClicked}/>
                <div className={`header-content${this.state.clicked ? '-true' : '-false'}`}>
                    <Link to="/list" style={{padding:"20px"}}>List</Link>
                    <Link to="/favorites" style={{padding:"20px"}}>Favorites</Link>
                    <Link to="/" style={{padding:"20px"}}> Home</Link>
               </div>
            </span>
        )
    }

}

export default Header
