import React, { useState, useEffect } from "react";
import Search from "../../components/Search";
import Header from "../../components/Header";
import "../../components/Search/Search.css";
import Movie_API from "../../api/Movie_API";
import MovieList from "../../components/Movies/MovieList";
import "../styles.css";
import Logo from "../../components/TMDB-Image";
import Favorites from "../../components/Movies/Favorite";
import { OverlayTrigger, Tooltip } from "react-bootstrap";
import MovieCard from "../../components/Movies/MovieCard";

const handleFavs = (fav) => {
  const arr = []
  const index2 = arr.indexOf(...fav.movie)

    fav.movie.map((index, key) => {
      if(arr.includes(...fav.movie) || fav.clicked === true){
        arr.splice(index2, 1)
      }else{
        arr.push(...fav.movie)
      }
    })
  }

function MovieInfo({ match }) {
  useEffect (() => {
    async function getData() {
      const [movieInfo, castAndCrewInfo, recommendInfo] = await Promise.all(
        [
          Movie_API.get(`/movie/${match.params.id}`, {
            params: {
              api_key: process.env.REACT_APP_API,
              language: "en-US",
            },
          }),

          Movie_API.get(`/movie/${match.params.id}/credits`, {
              params: {
                api_key: process.env.REACT_APP_API,
              },
            }),

            Movie_API.get(
              `/movie/${match.params.id}/recommendations`,{
                params: {
                  api_key: process.env.REACT_APP_API,
                },
              })
         ]
    )

    setInfo(movieInfo.data)
    setCastInfo(castAndCrewInfo.data.cast)
    setCrewInfo(castAndCrewInfo.data.crew)
    setRecInfo(recommendInfo.data.results)

    window.scrollTo(0, 0);

    };
    getData();
  },[match])



  const [info, setInfo] = useState({});
  const [castInfo, setCastInfo] = useState([]);
  const [crewInfo, setCrewInfo] = useState([]);
  const [recommendInfo, setRecInfo] = useState([]);



  return (

    <>
      <Header />
      <span className="info">
        <span style={{ display: "flex", flexDirection: "column" }}>
          <span className="title"></span>
          <MovieCard
              image={`https://image.tmdb.org/t/p/w185${info.poster_path}`}
              id={info.id}
              title={info.title}>
              <Favorites id={info.id}/>

              </MovieCard>


        </span>
        <div className="description">
          <span className="title">
            {info.title !== info.original_title ? (
              <h2> {`${info.title} [${info.original_title}]`}</h2>
            ) : (
              <h2> {info.original_title}</h2>
            )}

            <h2 style={{ marginLeft: "10px" }}>({info.release_date})</h2>
          </span>
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "auto auto auto",
              marginTop: "10px",
            }}
          >
            <h4>Rating: {info.vote_average * 10}%</h4>
            <h4>Popularity: {info.popularity}</h4>
            <h4>Runtime: {info.runtime} minutes</h4>
          </div>
          <h3 style={{ border: "none" }}>
            {info.tagline !== "" ? info.tagline : null}
          </h3>
          <p style={{ display: "inline-block", padding: "20px" }}>
            {" "}
            {info.overview === "" ? "No info here" : info.overview}{" "}
          </p>
        </div>
      </span>

      <div
        style={{
          width: "100%",
          height: "0px",
          border: "2px black solid",
          marginTop: "3em",
        }}
      ></div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          overflowX: "scroll",
          overflowY: "hidden",
          marginTop: "20px",
          marginBottom: "90px",
        }}
      >
        {castInfo.map((cast, key) => (
          <OverlayTrigger
            key={key}
            placement="bottom"
            overlay={
              <Tooltip
                key={key}
                style={{
                  paddingTop: "20px",
                  textAlign: "center",
                  justifyContent: "center",
                }}
              >
                {cast.character}({cast.name})
              </Tooltip>
            }
          >
            <div
              style={{ width: "100px", height: "122px", marginRight: "50px" }}
            >
              <img
                key={key}
                alt={cast.name}
                src={
                  cast.profile_path === null
                    ? "https://previews.123rf.com/images/urfandadashov/urfandadashov1805/urfandadashov180500070/100957966-photo-not-available-icon-isolated-on-white-background-vector-illustration.jpg"
                    : `https://image.tmdb.org/t/p/w185${cast.profile_path}`
                }
                style={{
                  borderRadius: "100%",
                  width: "95px",
                  height: "113px",
                  padding: "5px",
                }}
              />
            </div>
          </OverlayTrigger>
        ))}
      </div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          overflowX: "scroll",
          overflowY: "hidden",
          marginTop: "20px",
          marginBottom: "90px",
        }}
      >
        {crewInfo.map((crew, key) => (
          <OverlayTrigger
            key={key}
            placement="bottom"
            overlay={
              <Tooltip
                key={key}
                style={{ paddingTop: "20px", textAlign: "center" }}
              >
                {crew.name} ({crew.job})
              </Tooltip>
            }
          >
            <div
              style={{ width: "100px", height: "122px", marginRight: "50px" }}
            >
              <img
                key={key}
                alt={crew.name}
                src={
                  crew.profile_path === null
                    ? "https://previews.123rf.com/images/urfandadashov/urfandadashov1805/urfandadashov180500070/100957966-photo-not-available-icon-isolated-on-white-background-vector-illustration.jpg"
                    : `https://image.tmdb.org/t/p/w185${crew.profile_path}`
                }
                style={{
                  borderRadius: "50%",
                  width: "95px",
                  height: "113px",
                  padding: "5px",
                }}
              />
            </div>
          </OverlayTrigger>
        ))}
      </div>

      <div
        style={{
          width: "100%",
          height: "0px",
          border: "2px black solid",
          marginTop: "3em",
        }}
      ></div>

      <div style={{ textAlign: "center" }}>
        <h3>Recommendations</h3>
        <MovieList movies={recommendInfo} />
      </div>

      <div
        style={{
          width: "100%",
          height: "0px",
          border: "2px black solid",
          marginTop: "3em",
        }}
      ></div>

      <Search />

      <Logo />
    </>
  );
}

export default MovieInfo;
