import React, {useState} from 'react'
import Search from '../../components/Search'
import Header from '../../components/Header'
import MovieCard from '../../components/Movies/MovieCard'
import Icon from '../../components/Icon'
import '../../components/Search/Search.css'
import Logo from '../../components/TMDB-Image'
import Movie_API from '../../api/Movie_API'

let response;

const genre = [
    {
        "Action": 28,
        'Adventure': 12,
        "Animation" : 16,
        "Comedy": 35,
        "Crime": 80,
        "Documentary": 99,
        "Drama": 18,
        "Family" : 10751,
        "Fantasy": 14,
        "History": 36,
        "Horror": 27,
        "Music": 10402,
        "Mystery": 9648,
        "Romance": 10749,
        "Science Fiction": 878,
        "TV Movie": 10770,
        "Thriller": 53,
        "War": 10752,
        "Western": 37
    }
]

console.log(genre)



const submitButton = async (event) => {
        const KEY = process.env.REACT_APP_API

         response = await Movie_API.get('/discover/movie', {
            params:{
                api_key: KEY,
                language: 'en-US',
                include_adult: 'false',
                page: '1',
                with_genres: `${event.target.id}%2C%20`
            }
        })

        console.log(response)


}


const Filter = () => {
    const [checked, setChecked] = useState(false)

    const handleClick = () =>{ 
        setChecked(!checked)


    }



    return(
        <div>
           <Header />
           <Icon />
           <div style={{textAlign:"center"}}>
                Search using these filters ⬇️
           </div>
           <div>
            <form>
                    {Object.keys(genre).map(function(k, keys) {
                        return(
                            <div>
                                <label style={{display:"block", margin:'10px'}}>{k}
                                    <input type="checkbox" key={keys} name="genre" id={genre[k]} style={{margin: "0 5px 0 10px"}} onClick={handleClick} checked={checked}/>
                                </label>

                            </div>
                        )
                    })}


            </form>
            </div>

            <Logo />



        </div>
    )
}

export default Filter
