import React from "react";
import "./Search.css";
import Movie_API from "../../api/Movie_API";
import MovieList from "../Movies/MovieList";
import ClipLoader from "react-spinners/ClipLoader";

let response = "";

class Search extends React.Component {
  state = {
    query: "",
    page: 0,
    list: [],
    loading: true
  };

  onHandleChange = (event) => {
    this.setState({
      query: event.target.value,
    });
  };

  onTermSubmit = async (e) => {
    e.preventDefault();
    const KEY = process.env.REACT_APP_API;
    if (this.state.query !== "") {
      setTimeout(() => {
        this.setState({loading: true})
      },50000)


      response = await Movie_API.get("/search/movie", {
        params: {
          api_key: KEY,
          language: "en-US",
          include_adult: "false",
          page: "1",
          query: this.state.query,
        },
      });
      this.setState({
        list: response.data.results,
        loading: false
      });
    } else {
      this.setState({
        list: [],
      });
    }
  };



  render() {

    return (
      <div style={{ textAlign: "center" }}>
        <form
          onSubmit={
            this.onTermSubmit
          } /*style={{margin: "auto", maxWidth:'50%'}} */
        >
          <span className="input-searchicon">
            <span className="icon">
              <span
                className="fas fa-search" /*style={{position:'relative', bottom:'30px', left:'10px'}} */
              ></span>
            </span>

            <input
              style={{ width: "100%" }}
              className="search"
              value={this.state.query}
              placeholder="Search For Any Movie"
              onChange={this.onHandleChange}
              type="text"
            ></input>
          </span>
        </form>
        <MovieList movies={this.state.list} />
      </div>
    );
  }
}

export default Search;
